mapper.update:

update
stock_goods
set
inventory = inventory - 1,
sale = sale + 1
where id = #{stockGoodId}
and start_time  <=  #{killTime}
and end_time >= #{killTime}
and inventory > 0;

mapper.select:
select id, inventory, sale, `version`, title, price, start_time, end_time, create_time
from stock_goods
where status = 1
and start_time >= #{now}
and start_time  <=  #{end}
order by start_time asc
limit #{offset}, #{limit}
